TEST(NpnMgrTest, cannonical_const0)
{
  TvFunc func = TvFunc::make_zero(0);

  NpnMgr npnmgr;
  npnmgr.cannonical(func);

  vector<NpnMap> map_list;
  npnmgr.all_cmap(map_list);

  EXPECT_EQ( 1, map_list.size() );

  const NpnMap& xmap = map_list[0];
  EXPECT_EQ( 0, xmap.input_num() );

  EXPECT_TRUE( xmap.oinv() );
}

TEST(NpnMgrTest, cannonical_const1)
{
  TvFunc func = TvFunc::make_one(0);

  NpnMgr npnmgr;
  npnmgr.cannonical(func);

  vector<NpnMap> map_list;
  npnmgr.all_cmap(map_list);

  EXPECT_EQ( 1, map_list.size() );

  const NpnMap& xmap = map_list[0];
  EXPECT_EQ( 0, xmap.input_num() );

  EXPECT_FALSE( xmap.oinv() );
}

class NpnMgrTestWithParam :
  public ::testing::TestWithParam<int>
{
};

TEST_P(NpnMgrTestWithParam, func_test)
{
  int ni = GetParam();
  int ni_exp = 1U << ni;
  int ni_exp_exp = 1U << ni_exp;
  unordered_set<TvFunc> func_hash;
  for ( int sig: Range(ni_exp_exp) ) {
    vector<int> values(ni_exp);
    for ( int p: Range(ni_exp) ) {
      if ( sig & (1U << p) ) {
	values[p] = 1;
      }
      else {
	values[p] = 0;
      }
    }
    TvFunc f(ni, values);

    NpnMgr npnmgr;
    TvFunc cfunc0 = npnmgr.cannonical(f);

    vector<NpnMap> map_list;
    npnmgr.all_cmap(map_list);
    if ( map_list.empty() ) {
      cout << "F     = " << f << endl
	   << "cfunc = " << cfunc0 << endl;
    }

    NpnMap map = map_list[0];
    TvFunc cfunc = f.xform(map);

    if ( func_hash.count(cfunc) == 0 ) {
      func_hash.insert(cfunc);
    }
  }

  int exp_num[] = {
    1,
    2,
    4,
    14,
    222,
  };

  EXPECT_EQ( exp_num[ni], func_hash.size() );
}

TEST_P(NpnMgrTestWithParam, func_test2)
{
  int ni = GetParam();
  int ni_exp = 1U << ni;
  int ni_exp_exp = 1U << ni_exp;
  unordered_set<TvFunc> func_hash;
  for ( int sig: Range(ni_exp_exp) ) {
    vector<int> values(ni_exp);
    for ( int p: Range(ni_exp) ) {
      if ( sig & (1U << p) ) {
	values[p] = 1;
      }
      else {
	values[p] = 0;
      }
    }
    TvFunc f(ni, values);
    if ( func_hash.count(f) > 0 ) {
      continue;
    }

    NpnMgr npnmgr;
    TvFunc cfunc = npnmgr.cannonical(f);

    {
      vector<NpnMap> map_list;
      npnmgr.all_cmap(map_list);

      for ( auto map: map_list ) {
	TvFunc xformed_cfunc = f.xform(map);
	EXPECT_EQ( cfunc, xformed_cfunc );
	if ( cfunc != xformed_cfunc ) {
	  cout << "f = " << f << endl;
	}
      }
    }

    if ( ni > 0 ) {
      for ( PermGen pg(ni, ni); !pg.is_end(); ++ pg ) {
	for ( int op: { 0, 1 } ) {
	  for ( int ip: Range(ni_exp) ) {
	    NpnMap map(ni);
	    bool oinv = op ? true : false;
	    map.set_oinv(oinv);
	    for ( int i: Range(ni) ) {
	      bool iinv = (ip & (1U << i)) ? true : false;
	      map.set(i, pg(i), iinv);
	    }
	    TvFunc f1 = f.xform(map);
	    func_hash.insert(f1);

	    NpnMgr npnmgr;
	    TvFunc cfunc1 = npnmgr.cannonical(f1);

	    {
	      vector<NpnMap> map_list;
	      npnmgr.all_cmap(map_list);

	      for ( auto cmap: map_list ) {
		TvFunc xformed_cfunc1 = f1.xform(cmap);
		EXPECT_EQ( cfunc1, xformed_cfunc1 );
		if ( cfunc1 != xformed_cfunc1 ) {
		  cout << "f1 = " << f1 << endl;
		}
	      }
	    }
	    if ( cfunc != cfunc1 ) {
	      cout << "f  = " << f << endl
		   << "f1 = " << f1 << endl
		   << "map = " << map << endl;
	    }
	    EXPECT_EQ( cfunc, cfunc1 );
	  }
	}
      }
    }
    else {
      for ( int op: { 0, 1 } ) {
	NpnMap map(0);
	bool oinv = op ? true : false;
	map.set_oinv(oinv);
	TvFunc f1 = f.xform(map);
	if ( 0 ) {
	  cout << "F1 = " << f1 << endl
	       << " map = " << map << endl;
	}

	NpnMgr npnmgr;
	TvFunc cfunc1 = npnmgr.cannonical(f1);

	EXPECT_EQ( cfunc, cfunc1 );
      }
    }
  }
}

class NpnMgrRandomTest :
  public ::testing::TestWithParam<int>
{
public:

  std::mt19937 mRandGen;

};

TEST_P(NpnMgrRandomTest, func_test)
{
  int ni = GetParam();
  int ni_exp = 1U << ni;
  int ni_exp_exp = 1U << ni_exp;
  int nfunc = 10;
  int nperm = 5;
  int nphase = 5;

  if ( ni == 0 ) {
    return;
  }

  if ( ni >= 10 ) {
    nfunc = 2;
    nperm = 3;
    nphase = 2;
  }

  std::uniform_int_distribution<int> rd2(0, 1);
  for ( int i: Range(nfunc) ) {
    vector<int> values(ni_exp);
    for ( int p: Range(ni_exp) ) {
      if ( rd2(mRandGen) ) {
	values[p] = 1;
      }
      else {
	values[p] = 0;
      }
    }
    TvFunc f(ni, values);

    NpnMgr npnmgr;
    TvFunc cfunc = npnmgr.cannonical(f);

    {
      vector<NpnMap> map_list;
      npnmgr.all_cmap(map_list);

      if ( map_list.empty() ) {
	cout << "f = " << f << endl
	     << "cfunc = " << cfunc << endl;
	ASSERT_TRUE( false );
      }

      for ( auto map: map_list ) {
	TvFunc xformed_cfunc = f.xform(map);
	EXPECT_EQ( cfunc, xformed_cfunc );
      }
    }

    RandPermGen rpg(ni);
    for ( int j: Range(nperm) ) {
      vector<int> dst_map(ni);
      rpg.generate(mRandGen);
      for ( int k: Range(ni) ) {
	dst_map[k] = rpg.elem(k);
      }
      for ( int op: { 0, 1} ) {
	bool oinv = op ? true : false;
	for ( int k: Range(nphase) ) {
	  NpnMap map(ni);
	  map.set_oinv(oinv);
	  for ( int i: Range(ni) ) {
	    bool iinv = rd2(mRandGen) ? true : false;
	    map.set(i, dst_map[i], iinv);
	  }
	  TvFunc f1 = f.xform(map);

	  NpnMgr npnmgr;
	  TvFunc cfunc1 = npnmgr.cannonical(f1);

	  if ( cfunc != cfunc1 ) {
	    cout << "f  = " << f << endl
		 << "f1 = " << f1 << endl
		 << "map = " << map << endl;
	  }
	  EXPECT_EQ( cfunc, cfunc1 );
	}
      }
    }
  }
}

INSTANTIATE_TEST_SUITE_P(Test0to5,
			NpnMgrTestWithParam,
			::testing::Range(0, 5));

INSTANTIATE_TEST_SUITE_P(Test5to20,
			NpnMgrRandomTest,
			::testing::Range(5, 20));

TvFunc
str_to_func(int ni,
	    const string& func_str)
{
  int ni_exp = 1 << ni;
  vector<int> values(ni_exp);
  for ( int i: Range(ni_exp) ) {
    values[i] = func_str[i] == '1' ? 1 : 0;
  }
  return TvFunc(ni, values);
}

TEST(NpnMgrTest1, bug1)
{
  TvFunc func = str_to_func(4, "1011111011000000");

  NpnMgr npnmgr;
  TvFunc cfunc = npnmgr.cannonical(func);

  NpnMap map1(4);
  map1.set_oinv(true);
  TvFunc func1 = func.xform(map1);

  TvFunc cfunc1 = npnmgr.cannonical(func1);

  EXPECT_EQ( cfunc, cfunc1 );
}

